// src/BusBookingForm.js
import React, { useState } from 'react';
import './Booking.css'; // Import your CSS file for styling

const destinations = [
  { id: 1, name: 'Mumbai' },
  { id: 2, name: 'Pune' },
  { id: 3, name: 'Solapur' },
  { id: 4, name: 'Latur' },
];

const Seatbook = () => {
  const [startDestination, setStartDestination] = useState('');
  const [endDestination, setEndDestination] = useState('');
  const [seatType, setSeatType] = useState('Double');
  const [price, setPrice] = useState(0);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  
  const seatLayout = [
    { id: 1, type: 'Single', booked: false },
    { id: 2, type: 'Single', booked: false },
    { id: 3, type: 'Double', booked: false },
    { id: 4, type: 'Double', booked: false },
    { id: 5, type: 'Double', booked: false },
    // Add more seats as needed
  ];

  const calculatePrice = () => {
    let basePrice = 0;
    if (startDestination && endDestination) {
      // Set unique prices based on the route
      if (startDestination === 'Mumbai' && endDestination === 'Latur') {
        basePrice = 970;
      } else if (startDestination === 'Pune' && endDestination === 'Solapur') {
        basePrice = 1600;
      }
      // Add more routes as needed
    }
    const finalPrice = seatType === 'Single' ? basePrice + 300 : basePrice; // Example pricing logic
    setTotalPrice(finalPrice);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Booking Details:');
    console.log('From:', startDestination);
    console.log('To:', endDestination);
    console.log('Seat Type:', seatType);
    console.log('Total Price:', totalPrice);
  };

  return (
    <div className="container">
      <div className="booking wrap">
        <h1>Bus Ticket Booking</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="start">Select Start Destination:</label>
          <select
            id="start"
            value={startDestination}
            onChange={(e) => setStartDestination(e.target.value)}
            required
          >
            <option value="">--Select--</option>
            {destinations.map((dest) => (
              <option key={dest.id} value={dest.name}>{dest.name}</option>
            ))}
          </select>

          <label htmlFor="end">Select End Destination:</label>
          <select
            id="end"
            value={endDestination}
            onChange={(e) => setEndDestination(e.target.value)}
            required
          >
            <option value="">--Select--</option>
            {destinations.map((dest) => (
              <option key={dest.id} value={dest.name}>{dest.name}</option>
            ))}
          </select>

          <div>
            <label>Seat Type:</label>
            <label>
              <input
                type="radio"
                value="Single"
                checked={seatType === 'Single'}
                onChange={(e) => {
                  setSeatType(e.target.value);
                  calculatePrice();
                }}
              />
              Single Seat
            </label>
            <label>
              <input
                type="radio"
                value="Double"
                checked={seatType === 'Double'}
                onChange={(e) => {
                  setSeatType(e.target.value);
                  calculatePrice();
                }}
              />
              Double Seat
            </label>
          </div>

          <div>
            <h3>Seat Layout:</h3>
            <div className="seat-layout">
              {seatLayout.map((seat) => (
                <div
                  key={seat.id}
                  className={`seat ${seat.booked ? 'booked' : ''}`}
                  onClick={() => {
                    if (!seat.booked) {
                      setSelectedSeats([...selectedSeats, seat]);
                      seat.booked = true; // Mark seat as booked
                    }
                  }}
                >
                  {seat.type} {seat.booked ? 'Booked' : 'Available'}
                </div>
              ))}
            </div>
          </div>

          <h2>Total Price: â‚¹{totalPrice}</h2>
          <button type="submit" className="btn">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
};

export default Seatbook;
