// src/SignupForm.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Signup.css'; // Import your CSS file for styling

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const navigate = useNavigate(); 
  
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle signup logic here, e.g., API call
    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }
    
    console.log('Username:', username);
    console.log('Email:', email);
    console.log('Password:', password);
    // Add API call for signup here

    alert('Signup successful!');
    navigate('/login');
  };

  return (
    <div className="container">
      <div className="signup wrap">
        <h1>Signup</h1>
        <form onSubmit={handleSubmit}>
          <input
            placeholder="Username"
            id="username"
            name="username"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)} // Controlled input
            required // Make it required for validation
          />
          <input
            pattern="^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$"
            placeholder="Email"
            id="email"
            name="email"
            type="email" // Change type to "email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Controlled input
            required // Make it required for validation
          />
          <input
            placeholder="Password"
            id="password"
            name="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Controlled input
            required // Make it required for validation
          />
          <input
            placeholder="Confirm Password"
            id="confirmPassword"
            name="confirmPassword"
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)} // Controlled input
            required // Make it required for validation
          />
          <input
            value="Signup"
            className="btn"
            type="submit" // Submit button
          />
        </form>
        <p  className="bounce-text">Already have an account? <Link to="/login">Login</Link></p> {/* Link to signup page */}
      </div>
    </div>
  );
};

export default Signup;
