// src/LoginForm.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css'; // Import your CSS file for styling

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle login logic here, e.g., API call
    console.log('Email:', email);
    console.log('Password:', password);
    
    // Simulate successful login (replace this with actual login logic)
    const isLoginSuccessful = true; // Replace this with your API call response
    if (isLoginSuccessful) {
      navigate('/seatbook'); // Redirect to the Book Ticket page
    }
  };

  return (
    /* From Uiverse.io by MikeAndrewDesigner */ 
    <div className="container">
      <div className="login wrap">
        <h1>Login</h1>
        <form onSubmit={handleSubmit}>
          <input
            pattern="^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$"
            placeholder="Email"
            id="email"
            name="email"
            type="email" // Change type to "email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Controlled input
            required // Make it required for validation
          />
          <input
            placeholder="Password"
            id="password"
            name="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Controlled input
            required // Make it required for validation
          />
          <input
            value="Login"
            className="btn"
            type="submit" // Submit button
          />
        </form>
        <p>Don't have an account? <Link to="/signup">Sign up</Link></p> {/* Link to signup page */}
      </div>
    </div>
  );
};

export default Login;
