// src/Navbar.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
import './Navbar.css';
import 'font-awesome/css/font-awesome.min.css';

const Navbar = () => {
  const navigate = useNavigate(); // Initialize navigate function

  // Mock authentication state: Replace with real logic (e.g., from Redux, Context API, etc.)
  const isAuthenticated = false; // Change this to true if the user is logged in

  const handleBookBusClick = () => {
    if (!isAuthenticated) {
      alert('Please login to book a bus.');
      navigate('/login'); // Redirect to login page if not authenticated
    } else {
      navigate('/seatbook'); // Redirect to seat booking page if logged in
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <h1>
          <i className="fa fa-bus-alt" aria-hidden="true"></i> Bus Booking System
        </h1>
      </div>
      <ul className="navbar-links">
        <li>
          <Link to="/login">
            <i className="fa fa-sign-in" aria-hidden="true"></i> Login
          </Link>
        </li>
        <li>
          <Link to="/signup">
            <i className="fa fa-user-plus" aria-hidden="true"></i> Signup
          </Link>
        </li>
        <li>
          {/* Use onClick handler for conditional navigation */}
          <span onClick={handleBookBusClick} style={{ cursor: 'pointer' }}>
            <i className="fa fa-bus" aria-hidden="true"></i> Book a Bus
          </span>
        </li>
        <li>
          <Link to="/feedback">
            <i className="fa fa-comments" aria-hidden="true"></i> Feedback
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
