// src/LoginForm.js
import React, { useState } from 'react';
import './Login.css'; // Import your CSS file for styling

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      // Handle login logic here, e.g., API call
      console.log('Login - Username:', username);
      console.log('Login - Password:', password);
    } else {
      // Handle signup logic here, e.g., API call
      console.log('Signup - Firstname:', firstName);
      console.log('Signup - Username:', username);
      console.log('Signup - Password:', password);
      console.log('Signup - Confirm Password:', confirmPassword);
    }
  };

  return (
    <div className="container">
      <input type="checkbox" id="signup_toggle" onChange={() => setIsLogin(!isLogin)} />
      <form className="form" onSubmit={handleSubmit}>
        <div className={`form_front ${isLogin ? '' : 'hidden'}`}>
          <div className="form_details">Login</div>
          <input
            placeholder="Username"
            className="input"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            placeholder="Password"
            className="input"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className="btn">Login</button>
          <span className="switch">
            Don't have an account?
            <label className="signup_tog" htmlFor="signup_toggle">
              Sign Up
            </label>
          </span>
        </div>
        <div className={`form_back ${isLogin ? 'hidden' : ''}`}>
          <div className="form_details">SignUp</div>
          <input
            placeholder="Firstname"
            className="input"
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
          <input
            placeholder="Username"
            className="input"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            placeholder="Password"
            className="input"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <input
            placeholder="Confirm Password"
            className="input"
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <button className="btn">Signup</button>
          <span className="switch">
            Already have an account?
            <label className="signup_tog" htmlFor="signup_toggle">
              Sign In
            </label>
          </span>
        </div>
      </form>
    </div>
  );
};

export default Login;
