// src/FeedbackForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Feedback.css'; // Import your CSS for styling

const Feedback = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [rating, setRating] = useState(5);
  const [hoverRating, setHoverRating] = useState(0); // State to handle star hover effect
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Feedback:', { name, email, rating, message });

    alert('Feedback submitted successfully!');
    navigate('/'); // Redirect to home page or another desired page
  };

  // Render stars dynamically
  const renderStars = () => {
    return Array(5)
      .fill(0) // Create an array of 5 items
      .map((_, index) => {
        const starValue = index + 1;

        return (
          <span
            key={index}
            className={`star ${starValue <= (hoverRating || rating) ? 'filled' : ''}`}
            onClick={() => setRating(starValue)} // Set rating on click
            onMouseEnter={() => setHoverRating(starValue)} // Highlight stars on hover
            onMouseLeave={() => setHoverRating(0)} // Reset hover effect on leave
          >
            â˜…
          </span>
        );
      });
  };

  return (
    <div className="container">
      <div className="feedback wrap">
        <h1>Feedback</h1>
        <form onSubmit={handleSubmit}>
          <input
            placeholder="Your Name"
            id="name"
            name="name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)} // Controlled input
            required
          />
          <input
            placeholder="Your Email"
            id="email"
            name="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Controlled input
            required
          />
          <div className="rating">
            <label>Rating:</label>
            <div className="stars">{renderStars()}</div>
          </div>
          <textarea
            placeholder="Write your feedback..."
            id="message"
            name="message"
            rows="4"
            value={message}
            onChange={(e) => setMessage(e.target.value)} // Controlled input
            required
          ></textarea>
          <input value="Submit Feedback" className="btn" type="submit" />
        </form>
      </div>
    </div>
  );
};

export default Feedback;
