// src/FeedbackForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Feedback.css'; // Import your CSS for styling

const Feedback = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [rating, setRating] = useState(5);
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle feedback logic here (e.g., API call)
    console.log('Feedback:', { name, email, rating, message });

    // Simulate successful submission (replace with actual logic)
    alert('Feedback submitted successfully!');
    navigate('/'); // Redirect to home page or another desired page
  };

  return (
    <div className="container">
      <div className="feedback wrap">
        <h1>Feedback</h1>
        <form onSubmit={handleSubmit}>
          <input
            placeholder="Your Name"
            id="name"
            name="name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)} // Controlled input
            required
          />
          <input
            placeholder="Your Email"
            id="email"
            name="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Controlled input
            required
          />
          <label htmlFor="rating">Rating (1-5):</label>
          <input
            id="rating"
            name="rating"
            type="number"
            min="1"
            max="5"
            value={rating}
            onChange={(e) => setRating(e.target.value)} // Controlled input
            required
          />
          <textarea
            placeholder="Write your feedback..."
            id="message"
            name="message"
            rows="4"
            value={message}
            onChange={(e) => setMessage(e.target.value)} // Controlled input
            required
          ></textarea>
          <input
            value="Submit Feedback"
            className="btn"
            type="submit"
          />
        </form>
      </div>
    </div>
  );
};

export default Feedback;
